--------------------------------------------------------
--  DDL for Table EMPLOYEES_DEMO
--------------------------------------------------------

  CREATE TABLE EMPLOYEES_DEMO 
   (	EMPLOYEE_ID NUMBER, 
	FIRST_NAME VARCHAR2(100), 
	LAST_NAME VARCHAR2(100), 
	EMAIL VARCHAR2(100), 
	PHONE_NUMBER VARCHAR2(100), 
	HIRE_DATE DATE, 
	JOB_ID VARCHAR2(10 BYTE), 
	SALARY NUMBER, 
	COMMISSION_PCT NUMBER, 
	MANAGER_ID NUMBER(6,0), 
	DEPARTMENT_ID NUMBER(4,0)
   );
   create sequence employees_demo_seq start with 300;
REM INSERTING into EMPLOYEES_DEMO
SET DEFINE OFF;
COMMIT;
declare
  l_rows number := 20000000;
  l_cnt  number := 0;
  v_start_from number := 300;
begin
  while (l_cnt < l_rows)
  loop
      insert /*+ APPEND */ into employees_demo
      select v_start_from+rownum+l_cnt, first_name, last_name, email, phone_number, hire_date, job_id, salary+l_cnt, commission_pct+l_cnt/1000, manager_id, department_id
        from employees_demo
       where rownum <= l_rows-l_cnt;
      l_cnt := l_cnt + sql%rowcount;
      commit;
  end loop;
end;

